void mainImage(out vec4 o, vec2 u) {
    float f, i, r, e,
          t = iTime;
    vec3 p, z = iResolution;
    for(o*=i;
        i++<1e2;
        f += r = .01 + abs(--r)*.1,
        o += 1. / r)
        for(p = vec3((u-z.xy/(2. * 2.0 * zoom))/z.y * f, f+t),
            p += cos(t+p.yzx+p.zzx)*(.6 + 5.0 * amplitude),
            r =  cos(p.z),
            e = 1.6 + iterations;
            e < 32.;
            e += e )
            r += abs(dot(sin(t + p*e ), z/z)) / e;
    o = tanh(vec4(fireColor,1) * o * o / f / 7e6);
}



