    vec3 f4 = flame(u+vec2( 3.,0.),.4,light4,vec3(.9,.6,.9));
    vec3 f5 = flame(u+vec2( 1.5,0.),.5,light5,vec3(.9,.7,.3));
    vec3 f6 = flame(u+vec2( 0.,0.),.6,light6,vec3(.6,.8,.9));
    vec3 f7 = flame(u+vec2( -1.5,0.),.7,light7,vec3(1.,.8,.5));
    vec3 f8 = flame(u+vec2(-3.,0.),.8,light8,vec3(.9,.6,.9));

    vec3 C = f1+f2+f3+f4+f5+f6+f7+f8;
    O = vec4(C+C,1.0);

}

// Fire shader - Parametric flame effect

// @param amplification float 1.0 [0.0,5.0] "Verstaerkung"
// @param iterations float 0.5 [0.0, 1.0] "Iteration depth"
// @param amplitude float 0.5 [0.0, 1.0] "Wave amplitude"
// @param zoom float 0.5 [0.1, 1.0] "Zoom level"
// @param fireColor color [1.0, 0.3, 0.1] "Fire color"

/*
    @SnoopethDuckDuck -6 chars
    @Xor              -1 chars

    Thanks! :D

*/


void mainImage(out vec4 o, vec2 u) {
    float f, i, r, e,
          t = iTime;
    vec3 p, z = iResolution;
    for(o*=i;
